public class Part1 {
    public String dna = "";
    public static int findStopCodon(String dna, int startIndex, String stopCodon){
    
        if(dna.indexOf(stopCodon, startIndex+3) == -1){
            return -1;
        }
        int end = dna.indexOf(stopCodon, startIndex+3);
        if((end - startIndex) %3==0){
            return end;
        }else{
            return -1;
        }
    }
    
    public static String findGene(String dna, int where){
        if(where >= dna.length()){
            return "";
        }
        if(dna.indexOf("ATG", where) == -1){
            return "";
        }
        int startIndex = dna.indexOf("ATG", where);
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        int minIndex = 0;
        if(taaIndex == -1 || (tagIndex < taaIndex && tagIndex != -1)){
            minIndex = tagIndex;
        }else{
            minIndex = taaIndex;
        }
        if(minIndex == -1 || (tgaIndex < minIndex && tgaIndex !=-1)){
            minIndex = tgaIndex;
        }
        if(minIndex == -1){
            return "";
        }else{
            return dna.substring(startIndex, minIndex + 3);
        }
    }
    
    public static void printAllGenes(String dna){
        int startIndex = 0;
        while(true){
            String currentGene = findGene(dna, startIndex);
            if(currentGene.isEmpty()){
                break;
            }
            System.out.println(currentGene);
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
            
        }
        
    }
    public static int countGenes(String dna){
        int startIndex = 0;
        int count = 0;
        while(true){
            if(findGene(dna, startIndex).isEmpty()){
                break;
            }
            count+=1;
            startIndex = dna.indexOf(findGene(dna, startIndex), startIndex) + findGene(dna, startIndex).length();
        }
        return count;
    }
    public void test(){
        System.out.println(countGenes(dna));
}   
}